def derivate(f):
    # step1: 함수오브젝트 f는 입력으로 받은상태
    # step2: 함수오브젝트 f를 이용하여 df라는 함수를 정의
    def df(x):
        h=0.000000000001
        return (f(x+h)-f(x))/h
    # step3: 정의된 df를 리턴
    return df

